// Filter tasks that need document preparation
const items = $input.all();
const tasksNeedingPrep = [];

items.forEach(item => {
  const task = item.json;
  if (!task.deadline) return;

  const taskTime = new Date(task.deadline);
  const now = new Date();
  const hoursUntilTask = (taskTime - now) / (1000 * 60 * 60);

  // Prepare documents 2-4 hours before task
  if (hoursUntilTask >= 2 && hoursUntilTask <= 4) {
    // Detect task type
    const text = `${task.title} ${task.description || ''}`.toLowerCase();
    let taskType = 'general';
    let prepTime = 2;

    if (text.includes('flight') || text.includes('airport')) {
      taskType = 'flight';
      prepTime = 3; // Earlier for flights
    } else if (text.includes('meeting') || text.includes('call')) {
      taskType = 'meeting';
      prepTime = 2;
    } else if (text.includes('doctor') || text.includes('medical')) {
      taskType = 'medical';
      prepTime = 2;
    }

    tasksNeedingPrep.push({
      json: {
        ...task,
        taskType,
        prepTime,
        hoursUntilTask
      }
    });
  }
});

return tasksNeedingPrep;

const task = $input.first().json;
const documents = [];

// Prepare flight-specific documents
if (task.taskType === 'flight') {
  // Extract flight details
  const flightPattern = /([A-Z]{2}\d{1,4})/;
  const match = task.title.match(flightPattern);

  documents.push({
