}];

// Merge all document data
const items = $input.all();
const taskData = items[0].json;

// Add flight status if available
if (items[1]?.json?.data) {
  const flightData = items[1].json.data[0];
  taskData.documents[0].data = {
    ...taskData.documents[0].data,
    status: flightData.flight_status || 'Unknown',
    departure: flightData.departure.iata,
    arrival: flightData.arrival.iata,
    gate: flightData.departure.gate || 'TBD'
  };
}

// Add weather data if available
if (items[2]?.json?.forecast) {
  const weather = items[2].json.forecast.forecastday[0];
  taskData.documents.push({
